package week1day2;

public class Loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String name = "Manoj";
		
		for(int j=0; j<=10; j++) {
			
			System.out.println(name);
		}
		
		
		for (int i = 0; i <= 20; i++) {
			
			if (i%2!=0)
			
			System.out.println("The Odd Number is " + i);
			
		}

	}

}
